import React from 'react';

const Header = () => {
  const brand = {
    primary: '#7a1720', // maroon
    accent: '#ffffff',
  };

  const headerStyle = {
    backgroundColor: brand.primary,
    color: brand.accent,
    padding: '1rem 2rem',
    boxShadow: '0 2px 6px rgba(0,0,0,0.08)',
    position: 'sticky',
    top: 0,
    zIndex: 40,
  };

  return (
    <header style={headerStyle}>
      <div style={{ display: 'flex', alignItems: 'center', gap: 12, maxWidth: 1200, margin: '0 auto' }}>
        <div style={{ width: 52, height: 52, borderRadius: 26, background: brand.primary, display: 'flex', alignItems: 'center', justifyContent: 'center', boxShadow: 'inset 0 -2px 0 rgba(0,0,0,0.06)'}}>
          <span style={{ color: '#fff', fontWeight: 700 }}>MS</span>
        </div>
        <div>
          <div style={{ fontWeight: 800, fontSize: 18 }}>Marwa Safaris Africa</div>
          <div style={{ fontSize: 12, color: '#f3f4f6' }}>Authentic African journeys â€” Responsible, Luxurious, Memorable</div>
        </div>
        <div style={{ marginLeft: 'auto', display: 'flex', gap: 10 }}>
          <button style={{ background: 'transparent', color: '#fff', border: '1px solid rgba(255,255,255,0.18)', padding: '8px 12px', borderRadius: 8 }}>Packages</button>
          <button style={{ background: 'transparent', color: '#fff', border: '1px solid rgba(255,255,255,0.18)', padding: '8px 12px', borderRadius: 8 }}>Contact</button>
          <button style={{ background: '#fff', color: brand.primary, padding: '8px 12px', borderRadius: 8, fontWeight: 700 }}>Book Now</button>
        </div>
      </div>
    </header>
  );
};

export default Header;
