import React from 'react';

const Packages = () => {
  const packages = [
    {
      title: 'Classic Kenya Wildlife Safari',
      desc: 'Maasai Mara game drives, Lake Nakuru flamingos, All meals & transfers',
      img: 'https://images.unsplash.com/photo-1500534314209-a25ddb2bd429?auto=format&fit=crop&w=1200&q=60',
    },
    {
      title: 'Gorilla Trekking in Uganda',
      desc: 'Permits included, Guided treks, Luxury lodge',
      img: 'https://images.unsplash.com/photo-1528909514045-2fa4ac7a08ba?auto=format&fit=crop&w=1200&q=60',
    },
    {
      title: 'Zanzibar Beach & Spice Tour',
      desc: 'Beach villa, Stone Town guided tour, Spice farm visit',
      img: 'https://images.unsplash.com/photo-1507525428034-b723cf961d3e?auto=format&fit=crop&w=1200&q=60',
    },
    {
      title: 'Mount Kilimanjaro Climb (Machame Route)',
      desc: 'Professional guides, Full board, Scenic routes',
      img: 'https://images.unsplash.com/photo-1501785888041-af3ef285b470?auto=format&fit=crop&w=1200&q=60',
    },
    {
      title: 'Luxury Conservation Lodge Experience',
      desc: 'Private guides, Conservancy walks, Spa & fine dining',
      img: 'https://images.unsplash.com/photo-1500530855697-b586d89ba3ee?auto=format&fit=crop&w=1200&q=60',
    },
  ];

  const brand = {
    primary: '#7a1720',
  };

  const packageGrid = {
    maxWidth: 1200,
    margin: '1rem auto 3rem',
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
    gap: '1.25rem',
    padding: '1rem',
  };

  const cardStyle = {
    backgroundColor: '#fff',
    borderRadius: '12px',
    boxShadow: '0 10px 20px rgba(15,23,42,0.06)',
    overflow: 'hidden',
    display: 'flex',
    flexDirection: 'column',
  };

  const imgStyle = { width: '100%', height: 200, objectFit: 'cover' };
  const cardBody = { padding: '1rem 1rem 1.25rem', flex: '1 0 auto' };
  const bookBtn = {
    backgroundColor: brand.primary,
    color: '#ffffff',
    padding: '0.5rem 0.9rem',
    border: 'none',
    borderRadius: 8,
    cursor: 'pointer',
  };

  return (
    <section id="packages" style={{ padding: '0 1rem' }}>
      <div style={{ maxWidth: 1200, margin: '0 auto' }}>
        <h3 style={{ fontSize: 22, marginBottom: 12 }}>Popular Packages</h3>

        <div style={packageGrid}>
          {packages.map((p, idx) => (
            <article key={idx} style={cardStyle}>
              <img src={p.img} alt={p.title} style={imgStyle} />
              <div style={cardBody}>
                <h4 style={{ fontSize: 18, margin: '0 0 6px' }}>{p.title}</h4>
                <p style={{ color: '#6b7280', fontSize: 14, marginBottom: 10 }}>{p.desc}</p>

                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: 'auto' }}>
                  <div style={{ fontWeight: 700 }}>{/* price placeholder */}USD 1,800</div>
                  <div>
                    <button onClick={() => alert(`${p.title} added to booking cart (placeholder)`)} style={bookBtn}>Book</button>
                    <button onClick={() => alert('Open quick view / full itinerary (placeholder)')} style={{ marginLeft: 8, padding: '0.45rem 0.8rem', borderRadius: 8, border: '1px solid #e5e7eb', background: 'transparent', cursor: 'pointer' }}>View</button>
                  </div>
                </div>
              </div>
            </article>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Packages;
