import React from 'react';

const About = () => {
  return (
    <section id="about" style={{ maxWidth: 1200, margin: '2rem auto', padding: '0 1rem' }}>
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 420px', gap: 20, alignItems: 'center' }}>
        <div>
          <h3 style={{ fontSize: 20 }}>About Marwa Safaris Africa</h3>
          <p style={{ color: '#6b7280' }}>
            We are a locally-run travel company with deep knowledge of East, Central and Southern Africa. Our team of experienced guides, conservation experts and travel planners crafts responsible itineraries that support communities and protect wildlife.
          </p>

          <ul style={{ marginTop: 12, color: '#6b7280' }}>
            <li>Local offices & operators across Africa</li>
            <li>ISO-level safety & vetted vehicle fleets</li>
            <li>Custom groups, corporate travel & educational tours</li>
          </ul>
        </div>

        <div style={{ borderRadius: 12, overflow: 'hidden' }}>
          <img src="https://images.unsplash.com/photo-1455763916899-e8b50eca9967?auto=format&fit=crop&w=1200&q=60" alt="Guides" style={{ width: '100%', height: 260, objectFit: 'cover' }} />
        </div>
      </div>
    </section>
  );
};

export default About;
